mapper.select:
SELECT *
FROM employees e
WHERE e.department_id = #{idDepartment}

SELECT e.salary
FROM employees e
WHERE e.department_id = #{idDepartment} AND e.is_chief = TRUE;

SELECT *
FROM employees e
WHERE e.id = #{id}

SELECT e.employment_date
FROM employees e
WHERE e.id = #{id}

SELECT e.salary
FROM employees e
WHERE id = #{idEmployee}
